	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseSftAmbito:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseSftAmbito::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

	// symfony_i18n behavior
	
	/**
	 * Returns the culture.
	 *
	 * @return string The culture
	 */
	public function getCulture()
	{
	  return $this->culture;
	}
	
	/**
	 * Sets the culture.
	 *
	 * @param string  The culture to set
	 *
	 * @return SftAmbito
	 */
	public function setCulture($culture)
	{
	  $this->culture = $culture;
	  return $this;
	}
	
	/**
	 * Returns the "nombre" value from the current {@link SftAmbitoI18n}.
	 */
	public function getNombre($culture = null)
	{
	  return $this->getCurrentSftAmbitoI18n($culture)->getNombre();
	}
	
	/**
	 * Sets the "nombre" value of the current {@link SftAmbitoI18n}.
	 *
	 * @return SftAmbito
	 */
	public function setNombre($value, $culture = null)
	{
	  $this->getCurrentSftAmbitoI18n($culture)->setNombre($value);
	  return $this;
	}
	
	/**
	 * Returns the "descripcion" value from the current {@link SftAmbitoI18n}.
	 */
	public function getDescripcion($culture = null)
	{
	  return $this->getCurrentSftAmbitoI18n($culture)->getDescripcion();
	}
	
	/**
	 * Sets the "descripcion" value of the current {@link SftAmbitoI18n}.
	 *
	 * @return SftAmbito
	 */
	public function setDescripcion($value, $culture = null)
	{
	  $this->getCurrentSftAmbitoI18n($culture)->setDescripcion($value);
	  return $this;
	}
	
	/**
	 * Returns the current translation.
	 *
	 * @return SftAmbitoI18n
	 */
	public function getCurrentSftAmbitoI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : SftAmbitoI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setSftAmbitoI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setSftAmbitoI18nForCulture(new SftAmbitoI18n(), $culture);
	      $this->current_i18n[$culture]->setIdCultura($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setSftAmbitoI18nForCulture(SftAmbitoI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addSftAmbitoI18n($object);
	}

} // BaseSftAmbito
