	/**
	 * Method perform an INSERT on the database, given a SftAmbito or Criteria object.
	 *
	 * @param      mixed $values Criteria or SftAmbito object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{
    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseSftAmbitoPeer:doInsert:pre') as $sf_hook)
    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseSftAmbitoPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(SftAmbitoPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from SftAmbito object
		}

		if ($criteria->containsKey(SftAmbitoPeer::ID) && $criteria->keyContainsValue(SftAmbitoPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.SftAmbitoPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseSftAmbitoPeer:doInsert:post') as $sf_hook)
    {
      call_user_func($sf_hook, 'BaseSftAmbitoPeer', $values, $con, $pk);
    }

		return $pk;
	}

