    {
      if (false !== $sf_hook_retval = call_user_func($sf_hook, 'BaseSftAmbitoPeer', $values, $con))
      {
        return $sf_hook_retval;
      }
    }

		if ($con === null) {
			$con = Propel::getConnection(SftAmbitoPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(SftAmbitoPeer::ID);
			$selectCriteria->add(SftAmbitoPeer::ID, $criteria->remove(SftAmbitoPeer::ID), $comparison);

		} else { // $values is SftAmbito object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);

    // symfony_behaviors behavior
    foreach (sfMixer::getCallables('BaseSftAmbitoPeer:doUpdate:post') as $sf_hook)
