	/**
	 * Returns an array of arrays that contain columns in each unique index.
	 *
	 * @return array
	 */
	static public function getUniqueColumnNames()
	{
	  return array(array('codigo'));
	}

	// symfony_behaviors behavior
	
	/**
	 * Returns the name of the hook to call from inside the supplied method.
	 *
	 * @param string $method The calling method
	 *
	 * @return string A hook name for {@link sfMixer}
	 *
	 * @throws LogicException If the method name is not recognized
	 */
	static private function getMixerPreSelectHook($method)
	{
	  if (preg_match('/^do(Select|Count)(Join(All(Except)?)?|Stmt)?/', $method, $match))
	  {
	    return sprintf('BaseSftAmbitoPeer:%s:%1$s', 'Count' == $match[1] ? 'doCount' : $match[0]);
	  }
	
	  throw new LogicException(sprintf('Unrecognized function "%s"', $method));
	}

	// symfony_i18n behavior
	
	/**
	 * Returns the i18n model class name.
	 *
	 * @return string The i18n model class name
	 */
	static public function getI18nModel()
	{
	  return 'SftAmbitoI18n';
	}
	
	/**
	 * Selects a collection of {@link SftAmbito} objects with a {@link SftAmbitoI18n} translation populated.
	 *
	 * @param Criteria  $criteria
	 * @param string    $culture
	 * @param PropelPDO $con
	 * @param string    $join_behavior
	 *
	 * @return array
	 */
	static public function doSelectWithI18n(Criteria $criteria, $culture = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
	  $criteria = clone $criteria;
	
	  if (null === $culture)
	  {
	    $culture = sfPropel::getDefaultCulture();
	  }
	
	  // Set the correct dbName if it has not been overridden
	  if ($criteria->getDbName() == Propel::getDefaultDB()) {
	  	$criteria->setDbName(self::DATABASE_NAME);
	  }
	
	  SftAmbitoPeer::addSelectColumns($criteria);
	  $startcol = (SftAmbitoPeer::NUM_COLUMNS - SftAmbitoPeer::NUM_LAZY_LOAD_COLUMNS);
	  SftAmbitoI18nPeer::addSelectColumns($criteria);
	  $criteria->addJoin(SftAmbitoPeer::ID, SftAmbitoI18nPeer::ID, $join_behavior);
	  $criteria->add(SftAmbitoI18nPeer::ID_CULTURA, $culture);
	
	  foreach (sfMixer::getCallables('BaseSftAmbito:doSelectJoin:doSelectJoin') as $sf_hook)
	  {
	    call_user_func($sf_hook, 'SftAmbito', $criteria, $con);
	  }
	
	  $stmt = BasePeer::doSelect($criteria, $con);
		$results = array();
	
		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = SftAmbitoPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = SftAmbitoPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
	  		// See http://propel.phpdb.org/trac/ticket/509
	  		// $obj1->hydrate($row, 0, true); // rehydrate
	  	} else {
				$cls = SftAmbitoPeer::getOMClass(false);
				$obj1 = new $cls();
				$obj1->hydrate($row);
	      SftAmbitoPeer::addInstanceToPool($obj1, $key1);
			} // if $obj1 already loaded
	
			$key2 = SftAmbitoI18nPeer::getPrimaryKeyHashFromRow($row, $startcol);
			if ($key2 !== null) {
				$obj2 = SftAmbitoI18nPeer::getInstanceFromPool($key2);
				if (!$obj2) {
					$cls = SftAmbitoI18nPeer::getOMClass(false);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol);
					SftAmbitoI18nPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 already loaded
	
	      $obj1->setSftAmbitoI18nForCulture($obj2, $culture);
			} // if joined row was not null
	
			$results[] = $obj1;
		}
	
		$stmt->closeCursor();
	
		return $results;
	}

