	 * an empty collection; or if this SftAplicacion has previously
	 * been saved, it will retrieve related SftEstadisticaAplicacions from storage.
	 *
	 * This method is protected by default in order to keep the public
	 * api reasonable.  You can provide public methods for those you
	 * actually need in SftAplicacion.
	 */
	public function getSftEstadisticaAplicacionsJoinSftUsuario($criteria = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		if ($criteria === null) {
			$criteria = new Criteria(SftAplicacionPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collSftEstadisticaAplicacions === null) {
			if ($this->isNew()) {
				$this->collSftEstadisticaAplicacions = array();
			} else {

				$criteria->add(SftEstadisticaAplicacionPeer::ID_APLICACION, $this->id);

				$this->collSftEstadisticaAplicacions = SftEstadisticaAplicacionPeer::doSelectJoinSftUsuario($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(SftEstadisticaAplicacionPeer::ID_APLICACION, $this->id);

			if (!isset($this->lastSftEstadisticaAplicacionCriteria) || !$this->lastSftEstadisticaAplicacionCriteria->equals($criteria)) {
				$this->collSftEstadisticaAplicacions = SftEstadisticaAplicacionPeer::doSelectJoinSftUsuario($criteria, $con, $join_behavior);
			}
		}
		$this->lastSftEstadisticaAplicacionCriteria = $criteria;

		return $this->collSftEstadisticaAplicacions;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collSftConfPersonals) {
				foreach ((array) $this->collSftConfPersonals as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collSftCredencials) {
				foreach ((array) $this->collSftCredencials as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collSftEstadisticaAplicacions) {
				foreach ((array) $this->collSftEstadisticaAplicacions as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collSftConfPersonals = null;
		$this->collSftCredencials = null;
		$this->collSftEstadisticaAplicacions = null;
			$this->aSftCredencial = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseSftAplicacion:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseSftAplicacion::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

