	 */
	public function countSftPerfilI18ns(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(SftPerfilPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collSftPerfilI18ns === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(SftPerfilI18nPeer::ID, $this->id);

				$count = SftPerfilI18nPeer::doCount($criteria, false, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(SftPerfilI18nPeer::ID, $this->id);

				if (!isset($this->lastSftPerfilI18nCriteria) || !$this->lastSftPerfilI18nCriteria->equals($criteria)) {
					$count = SftPerfilI18nPeer::doCount($criteria, false, $con);
				} else {
					$count = count($this->collSftPerfilI18ns);
				}
			} else {
