	 * @param      SftPerfilI18n $l SftPerfilI18n
	 * @return     void
	 * @throws     PropelException
	 */
	public function addSftPerfilI18n(SftPerfilI18n $l)
	{
		if ($this->collSftPerfilI18ns === null) {
			$this->initSftPerfilI18ns();
		}
		if (!in_array($l, $this->collSftPerfilI18ns, true)) { // only add it if the **same** object is not already associated
			array_push($this->collSftPerfilI18ns, $l);
			$l->setSftPerfil($this);
		}
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collSftAccesos) {
				foreach ((array) $this->collSftAccesos as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collSftConfPersonals) {
				foreach ((array) $this->collSftConfPersonals as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collSftPerfilCredencials) {
				foreach ((array) $this->collSftPerfilCredencials as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collSftPerfilI18ns) {
				foreach ((array) $this->collSftPerfilI18ns as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collSftAccesos = null;
		$this->collSftConfPersonals = null;
		$this->collSftPerfilCredencials = null;
		$this->collSftPerfilI18ns = null;
			$this->aSftUo = null;
			$this->aSftAmbitoTipo = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseSftPerfil:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseSftPerfil::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

