	/**
	 * Returns the current translation.
	 *
	 * @return SftPerfilI18n
	 */
	public function getCurrentSftPerfilI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : SftPerfilI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setSftPerfilI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setSftPerfilI18nForCulture(new SftPerfilI18n(), $culture);
	      $this->current_i18n[$culture]->setIdCultura($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setSftPerfilI18nForCulture(SftPerfilI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addSftPerfilI18n($object);
	}

